import React, { Component } from "react";

import Modal from "../components/Modal/Modal";
import Backdrop from "../components/backdrop/backdrop/backdrop";
import "./Stock.css";

class StockPage extends Component {
  state = {
    creating: false,
  };

  constructor(props) {
    super(props);
    this.valueElRef = React.createRef();
    this.dateElRef = React.createRef();
  }

  startCreateEventHandler = () => {
    this.setState({ creating: true });
  };

  modalConfirmHandler = () => {
    this.setState({ creating: false });
    const value = this.valueElRef.current.value;
    const date = this.dateElRef.current.value;

    if (value <= 0 || date.trim().length === 0) {
      return;
    }

    const stock = { value, date };
    console.log(stock);
  };

  modalCancelHandler = () => {
    this.setState({ creating: false });
  };

  render() {
    return (
      <React.Fragment>
        {this.state.creating && <Backdrop />}
        {this.state.creating && (
          <Modal
            title="Add Event"
            canCancel
            canConfirm
            onCancel={this.modalCancelHandler}
            onConfirm={this.modalConfirmHandler}
          >
            <from>
              <div className="form-control">
                <label htmlFor="value">Value</label>
                <input type="text" id="value" ref={this.valueElRef} />
              </div>

              <div className="form-control">
                <label htmlFor="date">Date</label>
                <input type="datetime-local" id="date" ref={this.dateElRef}></input>
              </div>
            </from>
          </Modal>
        )}
        <div className="events-control">
          <p>Share your own Stock!</p>
          <button className="btn" onClick={this.startCreateEventHandler}>
            Create Event
          </button>
        </div>
      </React.Fragment>
    );
  }
}

export default StockPage;
