import React, { Component } from "react";
import TableEditMember from "../../containers/Member/Edit/Table_EditMember";
import Backedit from "../../containers/Member/Edit/back-edit";
import { Layout } from "antd";
import AuthContext from "../context/auth-context";
import styled from "styled-components";

const { Header, Content } = Layout;

class EditMember extends Component {
  state = {
    creating: false,
    stocks: [],
  };

  static contextType = AuthContext;

  constructor(props) {
    super(props);
    this.valueElRef = React.createRef();
    this.dateElRef = React.createRef();
  }

  componentDidMount() {
    this.fetchStocks();
  }

  fetchStocks() {
    const requestBody = {
      query: `
              query {
                stocks {
                  _id
                  date
                  value

                }
              }
            `,
    };

    fetch("http://localhost:8000/graphql", {
      method: "POST",
      body: JSON.stringify(requestBody),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => {
        if (res.status !== 200 && res.status !== 201) {
          throw new Error("Failed!");
        }
        return res.json();
      })
      .then((resData) => {
        const stocks = resData.data.stocks;
        this.setState({ stocks: stocks });
      })
      .catch((err) => {
        console.log(err);
      });
  }

  render() {
    return (
      <div>
        <Layout>
          <Header style={{ backgroundColor: "#F0F2F5", marginTop: "50px" }}>
            <Backedit />
          </Header>
          <Editmember>
            <div className="E-header " style={{ marginTop: "40px" }}>
              <c>ตรวจสอบ-แก้ไขข้อมูลสมาชิก55</c>
            </div>
            <div className="E-content">
              <Content>
                <TableEditMember />
              </Content>
            </div>
          </Editmember>
        </Layout>
      </div>
    );
  }
}
export default EditMember;

const Editmember = styled.div`
  .E-content {
    position: relative;
    background-color: #fefefe;
    padding: 10px 0px 0px 10px;
    width: auto;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
    height: auto;
    margin-bottom: 60px;
    margin-left: 40px;
    margin-right: 40px;
  }
  .E-header {
    margin-left: 40px;
    margin-right: 40px;
    width: auto;
    font-size: 3ch;
    padding: 10px 0px 0px 10px;
    background-color: #0080ff;
    color: white;
    height: 2.75rem;
    border: solid thick #0080ff;
    border-radius: 1em 1em 0em 0em;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
  }
`;
