                    title: "ลำดับที่",
                    dataIndex: "no",
                    className: "table_style",
                  },
                  {
                    title: "รหัสสมาชิก",
                    dataIndex: "member_code",
                    className: "table_style",
                  },
                  {
                    title: "ชื่อ",
                    dataIndex: "name",
                    className: "table_style",
                  },
                  {
                    title: "นามสกุล",
                    dataIndex: "surname",
                    className: "table_style",
                  },
                  {
                    title: "เลขประจำตัวประชาชน",
                    dataIndex: "identity_card",
                    className: "table_style",
                  },
                  {
                    title: "ประเภท",
                    dataIndex: "category",
                    className: "table_style",
