import React, { Component } from "react";

import "../Members/Login.css";
import AuthContext from "../../context/auth-context";

class AuthPage extends Component {
  state = {
    isMember: true
  };

  static contextType = AuthContext;


  constructor(props) {
    super(props);
    this.idEl = React.createRef();
    this.passwordEl = React.createRef();
  }
  switchModeHandler = () => {
    this.setState(prevState => {
      return { isMember: !prevState.isMember };
    });
  };
  submitHandler = (event) => {
    event.preventDefault();
    const id = this.idEl.current.value;
    const password = this.passwordEl.current.value;

    if (id.trim().length === 0 || password.trim().length === 0) {
      return;
    }

    let requestBody = {
      query: `
        query {
          login(id: "${id}", password: "${password}") {
            userId
            token
            tokenExpiration
          }
        }
        
      `,
    };

    if (!this.state.isMember) {
      requestBody = {
        query: `
        query {
          login_off(id: "${id}", password: "${password}") {
            userId
            token
            tokenExpiration
          }
        }
        `
      };
    }

    fetch('http://localhost:8000/graphql', {
      method: 'POST',
      body: JSON.stringify(requestBody),
      headers: {
        'Content-Type': 'application/json'
      }
    })
    .then(res => {
      if (res.status !== 200 && res.status !== 201) {
        
        throw new Error('Failed!');
      }
      return res.json();
    })
    .then(resData => {
      if(resData.data.login_off.token) {
        this.context.login_off(
          resData.data.login_off.token,
          resData.data.login_off.userId,
          resData.data.login_off.tokenExpiration
        );
      }
      
    })
      .catch((err) => {
        console.log(err);
      });
};
  render() {
    return (
      <form className="auth-form" onSubmit={this.submitHandler}>
        <div className="form-control">
          <label htmlFor="id">id</label>
          <input type="id" id="id" ref={this.idEl} />
        </div>
        <div className="form-control">
          <label htmlFor="password">Password</label>
          <input type="password" id="password" ref={this.passwordEl} />
        </div>
        <div className="form-actions">
          <button type="submit">Submit</button>
          <button type="button" onClick={this.switchModeHandler}>
            Switch to {this.state.isMember ? 'Officer' : 'Member'}
          </button>
        </div>
      </form>
    );
  }
}

export default AuthPage;
