import React, { Component } from "react";

import { Layout } from "antd";
import AuthContext from "../../context/auth-context";
import UserList from "../../components/Users/UserList/UserList";
import styled from "styled-components";

const { Header, Content } = Layout;

class EditMember extends Component {
  state = {
    creating: false,
    user: [],
  };

  static contextType = AuthContext;

  constructor(props) {
    super(props);
    this.First_nameEl = React.createRef();
    this.Last_nameEl = React.createRef();
    this.SexEl = React.createRef();
    this.Birth_DayEl = React.createRef();
    this.ID_cardEl = React.createRef();
    this.EmailEl = React.createRef();
    this.UsernameEl = React.createRef();
    this.PasswordEl = React.createRef();
    this.Tell_numberEl = React.createRef();
    this.Tell_number2El = React.createRef();
    this.FacebookEl = React.createRef();
    this.LineEl = React.createRef();
  }

  componentDidMount() {
    this.fetchuser();
  }

  fetchuser() {
    const requestBody = {
      query: `
              query {
                user {
                  _id
                  First_name
                  Last_name
                  ID_card

                }
              }
            `,
    };

    fetch("http://localhost:8000/graphql", {
      method: "POST",
      body: JSON.stringify(requestBody),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => {
        if (res.status !== 200 && res.status !== 201) {
          throw new Error("Failed!");
        }
        return res.json();
      })
      .then((resData) => {
        const user = resData.data.user;
        this.setState({ user: user });
      })
      .catch((err) => {
        console.log(err);
      });
  }

  render() {
    return (
      <div>
         <UserList user={this.state.user} />
      </div>
    );
  }
}
export default EditMember;

const Editmember = styled.div`
  .E-content {
    position: relative;
    background-color: #fefefe;
    padding: 10px 0px 0px 10px;
    width: auto;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
    height: auto;
    margin-bottom: 60px;
    margin-left: 40px;
    margin-right: 40px;
  }
  .E-header {
    margin-left: 40px;
    margin-right: 40px;
    width: auto;
    font-size: 3ch;
    padding: 10px 0px 0px 10px;
    background-color: #0080ff;
    color: white;
    height: 2.75rem;
    border: solid thick #0080ff;
    border-radius: 1em 1em 0em 0em;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
  }
`;
