import React, { Component } from "react";

import "../Members/Login.css";
import AuthContext from "../../context/auth-context";

class AuthPage extends Component {
  state = {
    isMember: true
  };

  static contextType = AuthContext;

  constructor(props) {
    super(props);
    this.First_nameEl = React.createRef();
    this.Last_nameEl = React.createRef();
    this.SexEl = React.createRef();
    this.Birth_DayEl = React.createRef();
    this.ID_cardEl = React.createRef();
    this.EmailEl = React.createRef();
    this.UsernameEl = React.createRef();
    this.PasswordEl = React.createRef();
    this.Tell_numberEl = React.createRef();
    this.Tell_number2El = React.createRef();
    this.FacebookEl = React.createRef();
    this.LineEl = React.createRef();
  }
  
  switchModeHandler = () => {
    this.setState(prevState => {
      return { isMember: !prevState.isMember };
    });
  };

  submitHandler = event => {
    event.preventDefault();
    var First_name = document.getElementById("First_name").value;
    var Last_name = document.getElementById("Last_name").value;
    var Sex = document.getElementById("Sex").value;
    var Birth_Day = document.getElementById("Birth_Day").value;
    var ID_card = document.getElementById("ID_card").value;
    var Email = document.getElementById("Email").value;
    var Username = document.getElementById("Username").value;
    var Password = document.getElementById("Password").value;
    var Tell_number = document.getElementById("Tell_number").value;
    var Tell_number2 = document.getElementById("Tell_number2").value;
    var Facebook = document.getElementById("Facebook").value;
    var Line = document.getElementById("Line").value;
   
 
 
   

    if (Username.trim().length === 0 || Password.trim().length === 0) {
      return;
    }

    let requestBody = {
      query: `
        query {
          login(First_name: "${First_name}", Last_name: "${Last_name}", Sex: "${Sex}", Birth_Day: "${Birth_Day}", ID_card: "${ID_card}", Email: "${Email}", Username: "${Username}", Password: "${Password}", Tell_number: "${Tell_number}", Tell_number2: "${Tell_number2}", Facebook: "${Facebook}", Line: "${Line}") {
            userId
            token
            tokenExpiration
          }
        }
        
      `,
    };

    if (!this.state.isMember) {
      requestBody = {
        query: `
        query {
          login_off(Username: "${First_name}", Last_name: "${Last_name}", Sex: "${Sex}", Birth_Day: "${Birth_Day}", ID_card: "${ID_card}", Email: "${Email}", Username: "${Username}", Password: "${Password}", Tell_number: "${Tell_number}", Tell_number2: "${Tell_number2}", Facebook: "${Facebook}", Line: "${Line}") {
            userId
            token
            tokenExpiration
          }
        }
        `
      };
    }

    fetch('http://localhost:8000/graphql', {
      method: 'POST',
      body: JSON.stringify(requestBody),
      headers: {
        'Content-Type': 'application/json'
      }
    })
    .then(res => {
      if (res.status !== 200 && res.status !== 201) {
        
        throw new Error('Failed!');
      }
      return res.json();
    })
    .then(resData => {
      if (this.state.isMember) {
        if (resData.data.login.token) {
          this.context.login(
            resData.data.login.token,
            resData.data.login.userId,
            resData.data.login.tokenExpiration
          );
        }
      }
      
      else if (!this.state.isMember) {
        if(resData.data.login_off.token) {
          this.context.login_off(
            resData.data.login_off.token,
            resData.data.login_off.userId,
            resData.data.login_off.tokenExpiration
          );
        }
      }
      
    })
      .catch((err) => {
        console.log(err);
      });
};
  render() {
    return (
      <form className="auth-form" onSubmit={this.submitHandler}>
        <div className="form-control">
          <label htmlFor="Username">Username</label>
          <input type="Username" id="Username" ref={this.UsernameEl} />
        </div>
        <div className="form-control">
          <label htmlFor="Password">Password</label>
          <input type="Password" id="Password" ref={this.PasswordEl} />
        </div>
        <div className="form-actions">
          <button type="submit">Submit</button>
          <button type="button" onClick={this.switchModeHandler}>
            Switch to {this.state.isMember ? 'Officer' : 'Member'}
          </button>
        </div>
      </form>
    );
  }
}

export default AuthPage;
