import React, { Component } from "react";
import Backmenuber from "../../containers/Officer/Addofficer/back-menu";
import Boxmenubar2 from "../../containers/Officer/Addofficer/box-memuber-2";
import Boxmenubar3 from "../../containers/Officer/Addofficer/box-memuber-3";
import styled from "styled-components";
import { UserOutlined } from "@ant-design/icons";

import { Row,Layout ,Modal,Button,Col, Form, Input, DatePicker, Radio, Select } from "antd";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { library } from "@fortawesome/fontawesome-svg-core";
import {
  fab,
  faLine,
  faFacebookSquare,
} from "@fortawesome/free-brands-svg-icons";
import "./Add.css";
library.add(fab);

const style = { padding: "10x 0px " };

const { Option } = Select;

function onChange(date, dateString) {
  console.log(date, dateString);
}

const { Header, Content } = Layout;
const { confirm } = Modal;
function showConfirm() {
  confirm({
    okText: "ใช่",
    cancelText: "ไม่ใช่",
    title: (
      <c style={{ fontFamily: "DB Adman X", fontSize: "2.6ch" }}>
        เพิ่มข้อมูลบุคลากร
      </c>
    ),
    icon: <UserOutlined />,
    content: (
      <c style={{ fontFamily: "DB Adman X", fontSize: "2.4ch" }}>
        คุณต้องการบันทึกข้อมูลหรือไม่
      </c>
